mapper.update:
UPDATE Common
SET info =
#{info}, price = #{price}, deadline = #{deadline}, min = #{min}

UPDATE 
	Common
SET image = #{param2}
WHERE 
	saleNo = #{param1}

UPDATE CommonJoin
SET count=#{count}, cjState = #{cjState}
where cjNo = #{cjNo}

mapper.select:
SELECT(cjNo, saleNo, userNo)
FROM CommonJoin
WHERE saleNo = #{saleNo}

SELECT cjNo
FROM CommonJoin
WHERE userNo = #{param1} AND saleNo = #{param2}
