mapper.update:
UPDATE Normal
SET info =#{info}, price = #{price}, count = #{count}
WHERE saleNo = #{saleNo}

UPDATE Normal
SET
saleState = #{param2}
WHERE saleNo = #{param1}

mapper.select:
SELECT saleState
FROM Normal
WHERE saleNo = #{saleNo}

SELECT userNo
FROM Normal
WHERE saleNo = #{saleNo}

SELECT pName, cateNo
FROM Product
WHERE pNo = #{pNo}
